	public void exportAsTXT() throws IOException{
		out.write("[!] BACKUPED "+sms.size()+ " SMS.");
		out.newLine();
		for (SMS s : sms) {
			out.append("From: "+s.getAddress() + " at: "+DateFormat.getDateTimeInstance(DateFormat.SHORT, DateFormat.SHORT).format(s.getDate())+ " with text: "+s.getText());
			out.newLine();
		}
		out.close();
	}

	public void exportAsCSV() throws IOException{
		out.write("address;date;text");
		out.newLine();
		for (SMS s : sms) {
			out.append(s.getAddress() + ";"+DateFormat.getDateTimeInstance(DateFormat.SHORT, DateFormat.SHORT).format(s.getDate())+ ";"+s.getText());
			out.newLine();
		}
		out.close();
	}
	
	public void setMessage(String messagge) throws IOException{
		this.message = (messagge);
	}

